import Matter from "matter-js";
import { getRenderHeight, getRenderWidth } from "./Size";
var WALL_WIDTH = getRenderWidth() * 10;
var WALL_HEIGHT = getRenderHeight() * 10;
export var WALL_BACK = Matter.Bodies.rectangle(getRenderWidth(), getRenderHeight(), getRenderWidth() * 2, getRenderHeight() * 2, { label: 'WALL_BACK', isStatic: true, isSensor: true, render: { fillStyle: 'none' } });
export var WALL_BOTTOM = Matter.Bodies.rectangle(getRenderWidth() / 2, (WALL_HEIGHT + getRenderHeight() * 2) / 2, WALL_WIDTH, WALL_HEIGHT, { label: 'WALL_BOTTOM', isStatic: true, friction: 1, render: { fillStyle: 'none' } });
export var WALL_LEFT = Matter.Bodies.rectangle(-WALL_WIDTH / 2, getRenderHeight() / 2, WALL_WIDTH, WALL_HEIGHT, { label: 'WALL_LEFT', isStatic: true, friction: 1, render: { fillStyle: 'none' } });
export var WALL_RIGHT = Matter.Bodies.rectangle(WALL_WIDTH / 2 + getRenderWidth(), getRenderHeight() / 2, WALL_WIDTH, WALL_HEIGHT, { label: 'WALL_RIGHT', isStatic: true, friction: 1, render: { fillStyle: 'none' } });
export var Wall = [WALL_BACK, WALL_BOTTOM, WALL_LEFT, WALL_RIGHT];
export default Wall;
